  display: inline-block;
  stroke: ${ThemeColors.transparent};
  fill: currentColor;
  vertical-align: middle;
  pointer-events: none;
  cursor: default;
`;

export const IconSvg = IconItem(({ name, className }) => {
  const Icon = IconList[name];
  return <Icon className={className} />;
});

